namespace Mautic\CoreBundle\Templating\Helper;

use Symfony\Component\Templating\Helper\Helper;

class MautibotHelper extends Helper
{
    public const OPEN_MOUTH     = 'openMouth';
    public const OPEN_MOUTH_SVG = 'data:image/png;base64,';
    public const SMILE          = 'smile';
    public const SMILE_SVG      = 'data:image/png;base64,';
    public const WAVE           = 'wave';
    public const WAVE_SVG       = 'data:image/png;base64,';

    /**
     * @param $image openMouth | smile | wave
     *
     * @return string
     */
    public function getImage($image)
    {
        return self::get($image);
    }

    /**
     * Static alternative to retrieve image.
     *
     * @param $image openMouth | smile | wave
     *
     * @return string
     */
    public static function get($image)
    {
        switch ($image) {
            case self::OPEN_MOUTH:
                return self::OPEN_MOUTH_SVG;

            case self::SMILE:
                return self::SMILE_SVG;

            case self::WAVE:
                return self::WAVE_SVG;
        }

        return '';
    }

    /**
     * {@inheritdoc}
     */
    public function getName()
    {
        return 'mautibot';
    }
}
